import { useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import styled from 'styled-components'

import { motion } from 'framer-motion'
import { pageAnimation } from '../animation'
import ScrollTop from '../components/ScrollTop'

//Images
import cineswipe from '../img/cineswipe1.png'
import cineswipe2 from '../img/cineswipe2.png'
import cineswipe3 from '../img/cineswipe3.png'

const CineSwipe = () => {
  return (
    <>
      <Details
        exit="exit"
        variants={pageAnimation}
        initial="hidden"
        animate="show"
      >
        <ScrollTop />
        <Headline>
          <h2>CineSwipe</h2>
          <img src={cineswipe3} alt="cineswipe" />
        </Headline>
        <Awards>
          <Award
            title="Le concept :  "
            description={`Vous avez du mal Ã  trouver quel film regarder avec votre compagnon ?  

            CineSwipe est la solution ! ðŸŽ¬
            
            A la maniÃ¨re d'un site de rencontre, il faut liker les films qu'on souhaite regarder.
            Lorsque les deux utilisateurs likent le meme film, il y a match ! â¤`} 
            key="principe"
          />
          <Award
            title="CodÃ©e en 24 heures"
            description="L'application est issue d'un dÃ©fi que je me suis posÃ© : coder une application mobile en React en seulement une journÃ©e"
            key="defi"
          />
          <Award
            title="Technologies utilisÃ©es : "
            description="L'application est dÃ©veloppÃ©e en React JS couplÃ© avec Firebase et l'usage d'une API"
            key="defi"
          />
        </Awards>
        <ImageDisplay>
          <iframe
            width="100%"
            height="700"
            src="https://www.youtube.com/embed/cd4ZnUT04T0"
            title="YouTube video player"
            frameborder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowfullscreen
          ></iframe>
        </ImageDisplay>
      </Details>
    </>
  )
}

const Details = styled(motion.div)`
  color: white;
`
const Headline = styled.div`
  min-height: 90vh;
  padding-top: 20vh;
  position: relative;
  h2 {
    position: absolute;
    top: 10%;
    left: 50%;
    transform: translate(-50%, -10%);
  }
  img {
    width: 100%;
    height: 70vh;
    object-fit: cover;
  }
`
const Awards = styled.div`
  min-height: 80vh;
  display: flex;
  margin: 5rem 10rem;
  align-items: center;
  justify-content: space-around;
  @media (max-width: 1500px) {
    display: block;
    margin: 2rem 2rem;
  }
`
const AwardStyle = styled.div`
  padding: 5rem;
  h3 {
    font-size: 2rem;
  }
  .line {
    width: 100%;
    background: #23d997;
    height: 0.5rem;
    margin: 1rem 0rem;
  }
  p {
    padding: 2rem 0rem;
  }
`

const ImageDisplay = styled.div`
  min-height: 50vh;
  img {
    width: 100%;
    height: 100vh;
    object-fit: cover;
  }
`
//Award component
const Award = ({ title, description }) => {
  return (
    <AwardStyle>
      <h3>{title}</h3>
      <div className="line"></div>
      <p>{description}</p>
    </AwardStyle>
  )
}
export default CineSwipe
