import React, { useState } from 'react'

import '../Contactform.css'

const ContactForm = () => {
  const [status, setStatus] = useState('Envoyer')
  const handleSubmit = async (e) => {
    e.preventDefault()
    setStatus('Envoi...')
    const { name, email, message } = e.target.elements
    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
    }
    let response = await fetch('http://localhost:5000/contact', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
      },
      body: JSON.stringify(details),
    })
    setStatus('Envoyer')
    let result = await response.json()
    alert(result.status)
  }
  return (
    <div className="wrapper">
      <form className="form">
        <div className="pageTitle title">Sign Up</div>
        <div className="secondaryTitle title">
          Please fill this form to sign up
        </div>
        <input type="text" className="name formEntry" placeholder="Name" />
        <input type="text" className="email formEntry" placeholder="Email" />
        <textarea
          className="message formEntry"
          placeholder="Message"
        ></textarea>
        <button className="submit formEntry" onclick="thanks()">
          Submit
        </button>
      </form>
    </div>
  )
}

export default ContactForm
